		 // g_form.addSubOption('u_what_needed','no','NO1');

	  }
	if(newValue == 'Parent2'){
		g_form.addOption('x_58872_needit_reference_field','child2.1','Child 2.1');
		g_form.addOption('x_58872_needit_reference_field','child2.2','Child 2.2');
		g_form.addOption('x_58872_needit_reference_field','child2.3','Child 2.3');

	}
	if(newValue== 'Parent3'){
		g_form.addOption('x_58872_needit_reference_field','child3.1','Child 3.1');
		g_form.addOption('x_58872_needit_reference_field','child3.2','Child 3.2');
