// 					if(newValue == g_form.getValue('u_requested_for'));
// 					{
// 					}
// 					 elseifalert('Valid User, Please enter your details.');
// 				}
// 			else alert('InValid User. Please return back !!');
// 					})());
		alert((function(){
			newValue='Abel Tuter';
			if((u_requested_for)== newValue){
				alert ("Valid User");
				g_form.setValue('u_string_2',emailFromScriptInclude);
			}
			else alert("InValid User");


		})());

			}
}
