sys_script_include.script:
var GetEmailAddress2 = Class.create();
GetEmailAddress2.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	getEmail : function(){
		var newRecord = new GlideRecord('sys_user');
		newRecord.get(this.getParameter('sysparm_userID'));
		return newRecord.email + '';
	},

    type: 'GetEmailAddress2'
});
